automatedFixConfig.gradleConfig:
plugins {
    id 'java'
    id 'maven-publish'
    id 'org.openrewrite.rewrite' version '6.8.4'
}

rewrite {
    activeRecipe(
        // Check for new methods on JDBC interfaces
        'org.openrewrite.java.migrate.JREJdbcInterfaceNewMethods',
        // Use the default InitialContext JNDI properties
        'org.openrewrite.java.liberty.RemoveWas2LibertyNonPortableJndiLookup',
        // MBean and MXBean interfaces must be public
        'org.openrewrite.java.migrate.MXBeanRule',
    )
}

dependencies {
        rewrite('org.openrewrite.recipe:rewrite-migrate-java:2.9.1')
        rewrite('org.openrewrite.recipe:rewrite-liberty:1.1.3')
}
